import React, { useState } from 'react';
import "./style-apply.css";

export default function Apply() {
  const [formData, setFormData] = useState({
    firstname: '',
    lastname: '',
    age: 18,
    gender: 'male',
    military_branch: 'army',
    rank: 'private',
    photo: null,
  });

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;

    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'file' ? files[0] : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    for (const key in formData) {
      formDataToSend.append(key, formData[key]);
    }

    try {
      const response = await fetch('http://localhost:3001/submit-application', {
        method: 'POST',
        body: formDataToSend,
      });

      const data = await response.json();
      console.log(data);
      // Обробка відповіді від сервера
    } catch (error) {
      console.error('Error submitting application:', error);
    }
  };

  return (
    <section className='support'>
      <div className="top-support"><p></p></div>
      <div className="left-support">
        <form className='form-support' onSubmit={handleSubmit} encType="multipart/form-data">
          {/* ... Ваші решта елементів форми залишаються незмінними */}
          <label htmlFor="firstname">Ім'я:</label>
          <input
            type="text"
            id="firstname"
            name="firstname"
            value={formData.firstname}
            onChange={handleChange}
            required
          />

          <label htmlFor="lastname">Фамілія:</label>
          <input
            type="text"
            id="lastname"
            name="lastname"
            value={formData.lastname}
            onChange={handleChange}
            required
          />

          {/* Додайте решту полів та їх значень за аналогією */}
          
          <label htmlFor="photo">Додати фото:</label>
          <input
            type="file"
            id="photo"
            name="photo"
            accept="image/*"
            onChange={handleChange}
            required
          />

          <button type="submit">Зареєструватися</button>
        </form>
      </div>
      <div className="right-support"></div>
    </section>
  );
}
