import React, { useState } from 'react';
import "./style-apply.css";

export default function Apply() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/submit-application', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email }),
      });

      const data = await response.json();
      console.log(data);
      // Додайте обробку відповіді від сервера, наприклад, відображення повідомлення про успішну заявку
    } catch (error) {
      console.error('Error submitting application:', error);
    }
  };

  return (
    <section className='support'>
      <div className="top-support"><p></p></div>
      <div className="left-support">
        <form className='form-support' onSubmit={handleSubmit}>
          <div className="text-support"><p>До заявки</p></div>
          <div className="line-support"></div>
          <div className="lbl-name"><label htmlFor="#">ПІБ</label></div>
          <div className="input-support-name">
            <input
              type="text"
              name="name"
              id="name"
              placeholder="Повне ім'я"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="lbl-mail"><label htmlFor="#">Ваша почта</label></div>
          <div className="input-support-mail">
            <input
              type="email"
              name="email"
              id="email"
              placeholder="Введіть почту"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="button-send">
            <button type="submit">Подати заявку</button>
          </div>
        </form>
      </div>
      <div className="right-support"></div>
    </section>
  );
}
