import React from 'react'
import logo from '../../img/logo.png'
import './style-header.css'

export default function Header() {
  const handlerLinkClick = (e) => {
    e.preventDefault();

    const id = e.target.getAttribute('href');
    const element = document.querySelector(id);
    console.log(element);
    const rect = element.getBoundingClientRect();
    console.log(rect);
    window.scrollTo({
      top: rect.top + window.pageYOffset,
      behavior: 'smooth',
    });

  };
  return (
    <header className='header'>
      <div className='left-header'>
        <img src={logo} alt="Бригада" />
      </div>
      <div className='right-header'>
        <ul className='menu-header'>
          <div className='list-header'><li ><a href="#">ГОЛОВНА</a></li></div>
          <div className='list-header'><li ><a href="#about-id" onClick={handlerLinkClick}>ПРО НАС</a></li></div>
          <div className='list-header'><li ><a href="#">SUPPORT</a></li></div>
          <div className='list-header'><li ><a href="#">Новини</a></li></div>
          <div className='list-header'><li ><a href="#">КОНТАКТИ</a></li></div>
        </ul>
      </div>
      <div className='join-header'>
        <div className="apply-header"><a href="#">ПОДАТИ ЗАЯВКУ</a></div>
      </div>

    </header>
  )
}
